import React from "react";
import { View, Image, StyleSheet } from "react-native";
import AppText from "../components/AppText";

import ListItem from "../components/ProductItem";
import colors from "../config/colors";

function ProductDetailsScreen(props) {
  return (
    <View>
      <Image style={styles.image} source={require("../assets/doghat.jpg")} />
      <View style={styles.detailsContainer}>
        <AppText style={styles.title}>Dog hat for sale</AppText>
        <AppText style={styles.price}>$10</AppText>
        <View style={styles.userContainer}>
          <ListItem
            image={require("../assets/banana.jpg")}
            title="Potato Wolf"
            subTitle="5 Listings"
          />
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  detailsContainer: {
    padding: 20,
  },
  image: {
    width: "100%",
    height: 300,
  },
  price: {
    color: colors.secondary,
    fontWeight: "bold",
    fontSize: 20,
    marginVertical: 10,
  },
  title: {
    fontSize: 24,
    fontWeight: "500",
  },
  userContainer: {
    marginVertical: 40,
  },
});

export default ProductDetailsScreen;
